package com.yafatek;import com.yafatek.Core.Items;import java.util.ArrayList;import java.util.List;public class OrderItems {	private List<Items> items = new ArrayList<>();	public void addItems(Items item) {		items.add(item);	}	public float getCost() {		float cost = 0.0f;		for (Items item : items) {			cost += item.price();		}		return cost;	}	public void showItems() {		for (Items item : items) {			System.out.println("Item: " + item.name());			System.out.println("Size: " + item.size());			System.out.println("Price: " + item.price());		}	}}